/**
 * @typedef {import('./type.d.ts').StimuliItem} StimuliItem
 * @typedef {import('./type.d.ts').StimuliPoolConfig} StimuliPoolConfig
 * @typedef {import('./type.d.ts').Timeline} Timeline
