/**
 * @returns {Params}
 */
function loadParams() {
  try {
    const stimuliItems = JSON.parse(_('stimuliItems'));
    const timeline = JSON.parse(_('timeline'));
    const totalRounds = _parse('totalRounds', 1);
    transformConcurrentPools(timeline, stimuliItems[0].pools.length);
    return {
      rounds: Array(totalRounds).fill().map(() => {
        const clone = deepClone(stimuliItems[0])
        clone.timeline = deepClone(timeline);
        return clone;
      }), // for now, use same timeline and stimuli for all rounds
      totalRounds,
      primes: JSON.parse(_('primes')),
      acceptedKeys: (_('acceptedKeys') || '').split(','),
      darkMode: _parse('darkMode', false),
    };
  } catch (e) {
    console.error(e);
    alert('Error when loading parameters. Error message:\n ' + e.toString());
    throw e;
  }
}




/**
 * @param {Params} params 
 */
function applyShuffle(params) {
  const shuffleResult = params.rounds.map(round => (
    round.pools.map(pool => {
      pool.shuffledIndexes = shuffledIndexes(pool);
      return pool.shuffledIndexes.map(x => x + 1).join(','); // to 1-based
    }).join(';')
  )).join('/');
  _set('shuffleResult', shuffleResult);
}

/**
 * @param {StimuliPoolConfig} pool  
 * @returns {number[]}
 */
function shuffledIndexes(pool) {
  const { items, shuffle } = pool;
  const indexes = getIndexesRepresentation(items);

  if (shuffle >= 1) {
    shuffleWithMaxRepeat(indexes, shuffle);
  } else if (shuffle) {
    shuffleArray(indexes);
  }
  return indexes;

  /**
   * Represent pool with indexes rather than the actual content, rotationally
   * e.g. [{count:2},{count:3},{count:1}] -> [0,1,2,0,1,1]
   * @param {{count: number}[]} items 
   * @returns {number[]}
   */
  function getIndexesRepresentation(items) {
    const result = [];
    const maxCount = items.map(x => x.count).reduce((x, y) => Math.max(x, y), 0);
    for (let numOfRotations = 0; numOfRotations < maxCount; numOfRotations++) {
      items.forEach(({ count }, index) => {
        if (count > numOfRotations) {
          result.push(index);
        }
      })
    }
    return result;
  }

  // Shuffles "array" in-place
  function shuffleArray(array) {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      const temp = array[i];
      array[i] = array[j];
      array[j] = temp;
    }
  }

  // Shuffles "array" in-place, with promise that no item will repeat "maxRepeat" times consecutively
  function shuffleWithMaxRepeat(array, maxRepeat) {
    const MAX_TRY = 100; // if cannot satisfy after too many tries, stop shuffling and ignore maxRepeat
    const validate = () => {
      let repeatItem, repeatTimes;
      for (let i of array) {
        if (i === repeatItem) {
          if (repeatTimes === maxRepeat) {
            return false;
          } else {
            repeatTimes++;
          }
        } else {
          repeatItem = i;
          repeatTimes = 1;
        }
      }
      return true;
    }
    for (let i = 0; i < MAX_TRY; i++) {
      shuffleArray(array);
      if (validate()) return;
    }
  }
}


/**
 * @param {Params} params
 */
function applyPrime(params) {
  const primeResult = findPrime(params.primes, params.rounds);
  applyAndOutputPrime(params, primeResult);
}

/** 
 * @typedef {{primeName: string, itemIndex: number|null}[]} PrimeResult 
 * itemIndex: 0-based, null means not found
 */
/**
 * Get locators for primes
 * @param {StimuliPrimeItem[]} primes
 * @param {StimuliRound[]} rounds
 * @returns {PrimeResult}
 */
function findPrime(primes, rounds) {
  if (primes === null) {
    return {};
  }
  /** @type {PrimeResult} */
  const primeResult = [];
  for (const { name, pool, include, exclude } of primes) {
    const includeIndexes = include && include.length ? (
      getItemIndex(include, primeResult)
    ) : (
      range(rounds[0].pools[pool - 1].items.length) // no item means include all in pool (for now, all rounds share same stimuli)
    );
    const excludeIndexes = getItemIndex(exclude || [], primeResult);
    const includeExcludeIndexes = includeExclude(includeIndexes, excludeIndexes);
    const itemIndex = randomChoice(includeExcludeIndexes);
    console.debug('prime include exclude', includeIndexes, excludeIndexes, '->', includeExcludeIndexes, '->', itemIndex);
    primeResult.push({ primeName: name, itemIndex });
  }
  console.debug('primeResult', primeResult);
  return primeResult;

  /**
   * @param {StimuliItemRef[]} itemRefs
   * @param {PrimeResult} primeResult 
   * @returns {number | null}
   */
  function getItemIndex(itemRefs, primeResult) {
    // (string (prime name) | itemIndexes)[]
    return itemRefs.map(ref => {
      if (typeof ref === 'string') {
        const found = primeResult.find(({ primeName }) => primeName === ref);
        return found ? found.itemIndex : null;
      } else {
        return ref - 1;
      }
    }).filter(x => x !== null);
  }

  function includeExclude(include, exclude) {
    const includeDedupe = [];
    for (const x of include) {
      if (!includeDedupe.includes(x)) {
        includeDedupe.push(x);
      }
    }
    return includeDedupe.filter(x => !exclude.includes(x));
  }

  function randomChoice(list) {
    return list.length ? list[Math.floor(Math.random() * list.length)] : null;
  }
}

/**
 * @param {Params} params
 * @param {PrimeResult} primeResult
 */
function applyAndOutputPrime(params, primeResult) {
  _set('primeResult', primeResult.map(({ primeName, itemIndex }) => primeName + '=' + (itemIndex + 1)).join(';'));
  _set('primeResultJson', JSON.stringify(Object.fromEntries(primeResult.map(({ primeName, itemIndex }) => [primeName, itemIndex + 1]))));

  for (const { primeName, itemIndex } of primeResult) {
    if (itemIndex !== null) {
      const prime = params.primes.find(p => p.name === primeName);
      if (typeof prime === 'object' && prime !== null) {
        const poolIndex = prime.pool - 1;
        // Apply stimuli count override
        if (typeof prime === 'object' && prime !== null && typeof prime.overrideCount === 'number') { // override same for all rounds
          params.rounds.forEach(round => {
            round.pools[poolIndex].items[itemIndex].count = prime.overrideCount;
          });
        }
        if (typeof prime === 'object' && prime !== null && Array.isArray(prime.overrideCount)) { // override differently for each round
          prime.overrideCount.forEach((roundOverrideCount, roundIndex) => {
            if (typeof roundOverrideCount === 'number') {
              params.rounds[roundIndex].pools[poolIndex].items[itemIndex].count = roundOverrideCount;
            }
          });
        }

        const stimuliItem0 = params.rounds[0].pools[poolIndex].items[itemIndex];
        _set('prime_' + primeName + '_item_index', itemIndex + 1);
        _set('prime_' + primeName + '_type', stimuliItem0.type);
        _set('prime_' + primeName + '_content', stimuliItem0.content);
        continue;
      }
    }
    _set('prime_' + primeName + '_item_index', 'null');
    _set('prime_' + primeName + '_type', 'null');
    _set('prime_' + primeName + '_content', 'null');
  }
}



/**
 * Transform the pool number from 1-indexed to 0-indexed in place. 
 * Value "0" becomes "-1" means display blank.
 * Generate dummy concurrentPool as default.
 * @param {Timeline} timeline
 */
function transformConcurrentPools(timeline, numOfPools) {
  if (timeline.concurrentPools) {
    timeline.concurrentPools.forEach(frame => {
      for (keyPoolMapping of frame) {
        if (typeof keyPoolMapping.pool === 'number') {
          keyPoolMapping.pool -= 1;
        }
      }
    });
  } else {
    // default: use "1" as element key, return [[{key:"1", pool:0}], [{key:"1", pool:1}], [{key:"1", pool:2}] ...]
    timeline.concurrentPools = range(numOfPools).map(i => [{key: '1', pool: i}]);
  }
}


/**
 * Preload images. Return a copy of the array but the items will be replaced with preloaded blobs asynchronously.
 * Preloaded images are stored in object `myGlobalThis.preload`
 * (We cannot directly use http cache to preload image, because Qualtrics sets the cache to expire in 60s (`cache-control: max-age=60`))
 * (Thus, use blobs.)
 * @param {Params} params
 * @returns {void}
 */
function applyPreload(params) {
  if (myGlobalThis.preload === undefined) {
    myGlobalThis.preload = {};
  }
  const imageContents = params.rounds.flatMap(({ pools }) =>
    pools.flatMap(({ items }) =>
      items.map(item => item.type === 'image' ? item.content : null).filter(x => x !== null)
    )
  );
  for (const content of imageContents) {
    // (even if already preloaded, do not skip; otherwise blob expire when restart survey in preview)
    // if content starts with 'http(s)://'
    // if (/^https?:\/\//.test(content)) {
    createImageBlob(content, (e, blob) => {
      if (blob) {
        console.debug('Preload blob created:', blob);
        myGlobalThis.preload[content] = blob;
      } else {
        console.debug('Preload blob error:', e);
      }
    });
    // } else {
    //   console.debug('Preload skipped for non-url content:', content)
    // }
  };

  function createImageBlob(url, callback) {
    const xhr = new XMLHttpRequest();
    xhr.open('GET', url, true);
    xhr.responseType = 'blob';
    xhr.onload = function () {
      if (this.status === 200 || this.status === 304) {
        callback(null, URL.createObjectURL(this.response));
      } else {
        callback(new Error('Request failed with status ' + this.status));
      }
    };
    xhr.onerror = function () {
      callback(new Error('Network error'));
    };
    xhr.send();
  }
}

/**
 * @param {string} name 
 * @returns {string|null}
 */
function _(name) {
  return Qualtrics.SurveyEngine.getEmbeddedData(name);
}

/**
 * Get embedded data as object, use default value if not exist. Similar to Python dict.get()
 * Throw exception when parse error.
 * @param {string} name 
 * @param {any} [defaultValue=null]
 * @returns {any}
 */
function _parse(name, defaultValue = null) {
  const stringData = _(name);
  if (stringData === null) {
    return defaultValue;
  } else {
    return JSON.parse(stringData);
  }
}

function _set(name, value) {
  console.log('[SPT] set ' + name + ' = ' + value);
  Qualtrics.SurveyEngine.setEmbeddedData(name, value);
}


/**
