 * @template K, V, W
 * @param {Record.<K, V>} obj 
 * @param {function(V): W} mapper 
 * @returns {Record.<K, W>}
 */
function mapObject(obj, mapper) {
  return Object.fromEntries(Object.entries(obj).map(([key, value]) => [key, mapper(value)]));
}


function range(n) {
  return Array(n).fill().map((_, index) => index);
}


function deepClone(item) {
  if (Array.isArray(item)) {
    return item.map(deepClone);
  }
  if (item instanceof Object) {
    const clone = {};
    Object.keys(item).forEach(key => {
      clone[key] = deepClone(item[key]);
    });
    return clone;
  }
  return item;
}
